select 
	regexp_replace(it2.title, E'[\\n\\r]+', ' ', 'g' ) as "title",
	it2.hrid as "instance HRID",
	it2.id as "instance UUID",
	hrt.id as "holdings UUID",
	it.id as "item_UUID",
	--it2."source" as "MARC shows in Discover, FOLIO does not.",
	--it2.discovery_suppress as "instance suppressed in Discover?",
	--hrt.discovery_suppress as "holdings suppressed in Discover?",
	--it.discovery_suppress as "item holdings suppressed in Discover?",
	ltb."name" as "permanent location",
	lta."name" as "effective location",
	it.effective_call_number_components__call_number as "call#",
	it.barcode as "barcode",
	regexp_replace(it.volume, E'[\\n\\r]+', ' ', 'g' ) as "volume",
	it.status__name as "status",
	substring(it.status__date from 0 for 11) as "status date",
	regexp_replace(itn.notes__note, E'[\\n\\r]+', ' ', 'g' ) as "note"
from inventory.item__t it
left join inventory.location__t lta on lta.id = it.effective_location_id
left join inventory.holdings_record__t hrt on hrt.id = it.holdings_record_id
left join inventory.location__t ltb on ltb.id = hrt.permanent_location_id
left join inventory.instance__t it2 on it2.id = hrt.instance_id 
left join inventory.item__t__notes itn on itn.id = it.id 
	and itn.notes__item_note_type_id = 'c7bc292c-a318-43d3-9b03-7a40dfba046a'
	and itn.notes__note <> it.volume 
left join inventory.item_note_type__t intt on intt.id = itn.notes__item_note_type_id
--where it.barcode in (select barcode from public.item)
	--and (it.status__name != 'Available' or it.effective_location_id not in ('5eb79fcc-af08-4ae1-9ab3-dde11e330a01', 'ed12a1d9-33e7-4c62-8daf-485e7d2369c3'))
